import React, { useState } from "react";
import {
  Container,
  TextField,
  Button,
  Typography,
  Card,
  CardContent,
  IconButton,
} from "@mui/material";
import ContentCopyIcon from "@mui/icons-material/ContentCopy";

function App() {
  const [longUrl, setLongUrl] = useState("");
  const [shortUrl, setShortUrl] = useState("");

  // Dummy shorten function (later weâ€™ll connect to backend)
  const handleShorten = () => {
    if (longUrl.trim() === "") return;
    const fakeShortUrl = "http://short.ly/" + Math.random().toString(36).substr(2, 6);
    setShortUrl(fakeShortUrl);
  };

  const handleCopy = () => {
    navigator.clipboard.writeText(shortUrl);
    alert("Short URL copied to clipboard!");
  };

  return (
    <Container maxWidth="sm" style={{ marginTop: "50px" }}>
      <Card elevation={4} style={{ padding: "20px", borderRadius: "15px" }}>
        <CardContent>
          <Typography variant="h4" align="center" gutterBottom>
            ðŸš€ URL Shortener
          </Typography>

          <TextField
            fullWidth
            label="Enter your long URL"
            variant="outlined"
            value={longUrl}
            onChange={(e) => setLongUrl(e.target.value)}
            style={{ marginBottom: "20px" }}
          />

          <Button
            fullWidth
            variant="contained"
            color="primary"
            onClick={handleShorten}
          >
            Shorten URL
          </Button>

          {shortUrl && (
            <Card
              elevation={2}
              style={{
                marginTop: "20px",
                padding: "10px",
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
              }}
            >
              <Typography variant="body1">{shortUrl}</Typography>
              <IconButton onClick={handleCopy}>
                <ContentCopyIcon />
              </IconButton>
            </Card>
          )}
        </CardContent>
      </Card>
    </Container>
  );
}

export default App;
